SELECT C.CARD_CODE, C.CARD_NAME, C.CARD_TYPE, C.ANNUAL_FEE,
B.ALWAYS_DISCOUNT, B.CVS, B.AIR_MILEAGE, B.BEAUTY, B.COMMUNICATION,
B.CAFE_BAKERY, B.MOVIE, B.HYPERMART, B.EDUCATION, B.CULTURE,
B.ANNUAL_FEE_SUPPORT, B.PUBLIC_TRANSPORTATION, B.RENTAL, B.OIL,
B.SHOPPING, B.LEISURE, B.NATIONAL_VOUCHER, B.POINT_CASHBACK,
B.AUTOMOBILES, B.MEDICAL, B.FINANCE, B.EAT_OUT, B.EASY_PAY,
B.MAINTENANCE, B.PREMIUM, B.VOUCHER, B.PRIORITY_PASS FROM CARD
C, CARD_BENEFITS B WHERE C.CARD_NAME=B.CARD_NAME


	AND C.CARD_NAME like '%'||#{cardName}||'%'


	AND C.CARD_TYPE = #{cardType}


